package org.osm2world.core.target.common.material;

import static org.junit.Assert.assertEquals;
import static org.osm2world.core.target.common.texcoord.NamedTexCoordFunction.GLOBAL_X_Z;

import java.awt.*;

import org.junit.Test;

public class DataUriTextureTest {

	@Test
	public void testPngImage() {

		var uri = "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVQYV2NgYAAAAAMAAWgmWQ0AAAAASUVORK5CYII=";
		var texture = new DataUriTexture(uri, new TextureDataDimensions(1, 1), TextureData.Wrap.REPEAT, GLOBAL_X_Z);
		var image = texture.createBufferedImage();

		assertEquals(1, image.getHeight());
		assertEquals(1, image.getWidth());
		assertEquals(0, new Color(image.getRGB(0, 0), true).getAlpha());

	}

	@Test
	public void testJpgImage() {

		var uri = "data:image/jpg;base64,";
		var texture = new DataUriTexture(uri, new TextureDataDimensions(1, 1), TextureData.Wrap.REPEAT, GLOBAL_X_Z);
		var image = texture.createBufferedImage();

		assertEquals(1, image.getHeight());
		assertEquals(1, image.getWidth());
		assertEquals(255, new Color(image.getRGB(0, 0)).getGreen());

	}

}